import React from "react";
import "./styles.scss";

import Header from "./Header";
import Show from "./Show";
import Empty from "./Empty";
import useVisualMode from "../../hooks/useVisualMode";
import Form from "./Form";
import Status from "./Status";
import Confirm from "./Confirm";
import Error from "./Error";


export default function Appointment(props) {
  const EMPTY = "EMPTY";
  const SHOW = "SHOW";
  const CREATE = "CREATE";
  const SAVING = "SAVING";
  const CONFIRM = "CONFIRM";
  const EDIT = "EDIT";
  const ERROR_SAVE = "ERROR_SAVE";
  const ERROR_DELETE = "ERROR_DELETE";
  const DELETING = "DELETING";

  //destructure the required mode and functions from useVisualMode
  const { mode, transition, back } = useVisualMode(
    props.interview ? SHOW : EMPTY
  );

  function save(name, interviewer) {
    const interview = {
      student: name,
      interviewer
    };
    transition(SAVING);

    //in Application.js, we used return axios which will return a promise, so we need to use .then() here,  .then uses an anonymous callback function
    props.bookInterview(props.id,interview)
    .then(()=>{transition(SHOW)})
    .catch(() => transition(ERROR_SAVE,true));
  }

  //remove function
  function remove(){
    transition(DELETING,true);

    props.cancelInterview(props.id)
    .then(() => transition(EMPTY))
    .catch(() => {
      transition(ERROR_DELETE,true)

     });

  }

  return (
    <article className="appointment" data-testid="appointment">
      <Header time={props.time}/>
      {mode === EMPTY && <Empty onAdd={() => transition(CREATE)} />}
      {mode === SHOW && (
      <Show
        interview={props.interview}
        onDelete={()=>transition(CONFIRM)}
        //for editing the appointment
        onEdit={()=>transition(EDIT)}
      />
      )}
      {mode === CREATE && (
        <Form
         interviewer={props.interviewer}
         interviewers={props.interviewers}

         onCancel={() => back(EMPTY)}

         bookInterview={props.bookInterview} 
         onSave={save}

         />

      )}
      {mode === SAVING && (
        <Status message="Saving"/>
      )}
       {mode === CONFIRM && (
        <Confirm
        onConfirm={remove}
        onCancel={back}
        message="Are you sure you would like to delete?"
        />
      )}

      {mode === EDIT && (
        <Form
        name={props.name ? props.name : props.interview.student}
        interviewer={props.interview.interviewer.id}

        interviewers={props.interviewers}
        onSave={save}
        onCancel={back}
      />
      )}
        {mode === DELETING && (
        <Status
          message="Deleting"
        />
      )}

      {mode === ERROR_SAVE && (
        <Error
          message="Could not save appointment."
          onClose={back}
        />
      )}

      {mode === ERROR_DELETE && (
        <Error
          message="Could not cancel appointment."
          onClose={back}
        />
      )}


    </article>
  )
}
